mapper.select:
select * from SYS_ACCESS_URI E where E.ID_ = (
    select min(F.ID_) from SYS_ACCESS_URI F where F.URI_ = #{uri}
)

select distinct E.*



		order by ${orderBy}


		order by E.ID_ desc

mapper.sql:
from SYS_ACCESS_URI E


	1 = 1


